/* ------------------------------------------ */
/* ------- Totaliser App ------- */
/* ------------------------------------------ */
/* ------- Imports ------- */
/* ------- Sizing Variables ------- */
/* ------- Colors ------- */
/* ------------------------------*/
/* ----- Basic Setup ----- */
/* ------------------------------*/
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  font-family: inherit;
  box-sizing: inherit;
}

html {
  font-size: 62.5%;
}

body {
  font-family: "Quicksand", sans-serif;
  font-size: 1.8rem;
  color: #bcbfc0;
  box-sizing: border-box;
}

/* ------------------------------*/
/* ----- Components ----- */
/* ------------------------------*/
.btn {
  display: inline-block;
  float: left;
  width: 25%;
  padding: 2rem 3rem;
  font-size: 3rem;
  font-weight: 300;
  color: inherit;
  outline: none;
  border: none;
  background: #10151b;
  z-index: 1;
}

/* ------------------------------*/
/* ----- Main ----- */
/* ------------------------------*/
main {
  height: 100vh;
  background-image: linear-gradient(rgba(13, 14, 17, 0.6), rgba(13, 14, 17, 0.6)), url("681cd291191bceadc53297fcd9de6093.jpg");
  background-size: cover;
  background-position: top;
}

/* ----- Totaliser ----- */
.totaliser {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  max-width: 390px;
  max-height: 600px;
  min-width: 320px;
}
@media (max-width: 420px) {
  .totaliser {
    width: 100%;
  }
}
.totaliser__output {
  position: relative;
  padding: 8rem 2rem 2rem 2rem;
  font-weight: 400;
  text-align: right;
  background: #0d0e11;
  opacity: 0.85;
}
.totaliser__output .output--main {
  font-size: 7rem;
  font-weight: 300;
}
.totaliser__output .output--sub {
  word-spacing: 0.5rem;
  letter-spacing: 0.2rem;
}
.totaliser__output img {
  position: absolute;
  top: 0;
  left: 3%;
  height: 5rem;
  margin-top: -0.8rem;
}
.totaliser__controlls {
  background: #16171b;
  opacity: 0.9;
}
.totaliser__controlls::after {
  display: block;
  content: "";
  clear: both;
}
